sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
		if ( newValue == '') {
			return;
		}

		var subcategory = g_form.getValue('u_subcategory_1');

		// Clear all of the choices from the Subcategory field choice list
		g_form.clearOptions('u_subcategory_1');

		// If the value of the Category field is Active Directory, add
		// two AD choices to the Subcategory field choice list
		if(newValue == 'ad1'){
			g_form.addOption('u_subcategory_1','ad1','Account Disable');
			g_form.addOption('u_subcategory_1','ad2','Reset Password');
		}
		// If the value of the Category field is Microsoft Officce, add
		// two MO choices to the Subcategory field choice list
		if(newValue == 'm1'){
			g_form.addOption('u_subcategory_1','m1','Excel issues');
			g_form.addOption('u_subcategory_1','m2','Outlook Issues');
		}
		// If the value of the Category field is Hardware, add
		// two H choices to the Subcategory field choice list
		if(newValue == 'h1'){
			g_form.addOption('u_subcategory_1','h1','Laptop');
			g_form.addOption('u_subcategory_1','h2','CPU');
		}

		// If the form is loading and it is not a new record, set the u_subcategory_1 value to the
		// value from the record before it was loaded
		if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('u_subcategory_1', subcategory);
		}
	}
